import styled from 'styled-components'

const Section = styled.section`
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Score = styled.article`
    width: 100%;
    height: 50px;

    background-color: #c2c2c2;
`;

export default function Content() {
    const [score, setScore] = useState(0);
  return (
    <main>
        <Section>
            <Score>{score}</Score>
        </Section>
    </main>
  )
}
