import React, { useEffect, useRef, useState } from 'react';
import { createPortal } from "react-dom";
import styled from 'styled-components';

const Modal = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    position: fixed;
    top: 0;
    left: 0;
    z-index: 11;
    width: 100%;
    height: 100%;
`;

const ModalContent = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  z-index: 10;
  width: 300px;
  height: 300px;
  border: 1px solid;
  background: white;
`;

const ModalContentClose = styled.div`
  display: inline-flex;
  justify-content: flex-end;
  width: 100%;
`;

const ModalContentMain = styled.div`
  display: flex;

    padding: 1rem;
`;

export default function ModalPortal({children, closePortal}) {
    const ref = useRef(null);
    const [mounted,setMounted] = useState(false);

  useEffect(()=>{
    setMounted(true);
    if(document) {
        const dom = document.getElementById('root-modal');
        ref.current = dom; // ref에 dom 값 전달
    }
  },[]);

  if(ref.current&&mounted) { // mounted 됬고 dom이 존재하는 경우 모달 랜더링 진행
    return createPortal(
        <div className="modal">
            <div
            className="modal-background"
            role="presentation"
            onClick={closePortal}
            />
            <div className="modal-content">
            <div className="modal-content__close">
                <button onClick={closePortal}>x</button>
            </div>
            <div className="modal-content__main">{children}</div>
            </div>
      </div>,
      ref.current
    )
  }

  return null;
}
